export const renderField = ({
  input,
  label,
  type,
  meta: { touched, error, warning },
}) => (
  <div>
    <label>{label}</label>
    <div className="col-md6 form-group " {...formItemLayout}>
      <input
        {...input}
        placeholder={label}
        type={type}
        className="form-control"
      />
      {touched && error && <span className="text-danger">{error}</span>}
    </div>
  </div>
);
