import { React, Component, Layout, connect } from "../../global";
import { Space, Card, Row, Col } from 'antd';
import PublicHeader from "../../components/public/header/PublicHeader";
import INGREDIENT from "../../redux/actions/public/ingredientAction";
const { Footer, Content } = Layout;

class HomePage extends Component {
  render() {
    console.log('props', this.props.ingredient);
    const gridStyle = {
      padding: "1rem"
    };
    if (this.props.ingredient) {
      return (
        <div>
          <Layout>
            <PublicHeader />
            <Content
              width={300}
              style={{
                height: "840px",
                textAlign: "center",
                backgroundColor: "#eeeff1",
              }}
            >



              <Card title="Place the ingredients on the pizza."  >
                <Row>
                  {this.props.ingredient.map((data, index) => {
                    return (

                      <Col span={6} key={index} >
                        <Card title={data.name} hoverable={true} gridStyle >
                          <p>Price : {data.price}</p>
                          <p>Time to make pizza : {data.time}</p>
                        </Card>
                      </Col>
                    );
                  })}
                </Row>
              </Card>
              <Steps current={current}>
                {steps.map(item => (
                  <Step key={item.title} title={item.title} />
                ))}
              </Steps>
              <div className="steps-content">{steps[current].content}</div>
              <div className="steps-action">
                {current < steps.length - 1 && (
                  <Button type="primary" onClick={() => next()}>
                    Next
                  </Button>
                )}
                {current === steps.length - 1 && (
                  <Button type="primary" onClick={() => message.success('Processing complete!')}>
                    Done
                  </Button>
                )}
                {current > 0 && (
                  <Button style={{ margin: '0 8px' }} onClick={() => prev()}>
                    Previous
                  </Button>
                )}
              </div>


            </Content>
            <Footer style={{ textAlign: "center" }}>
              Design Â©2020 Created by Nikola Mrkic
            </Footer>
          </Layout>
        </div>
      );
    } else {
      return (<div>
      </div>)
    }

  }
}

// Map State To Props (Redux Store Passes State To Component)
const mapStateToProps = (state) => {
  console.log('state 1', state);
  const fethcSuccess = state.ingredient.success;
  // Redux Store --> Component
  if (fethcSuccess) {
    const ingredient = state;
    return {
      ingredient: ingredient.ingredient.data,
    };
  }
};

// anything returned from this function will end up as props
function mapDispatchToProps(dispatch) {
  return {
    getIngredient: dispatch(INGREDIENT.request())
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
