import { React, useState, Table, Modal, Button } from "../../../global";
import UserForm from "../forms/User/UserForm";
import USER_ADMIN from "../../../redux/actions/admin/userAction";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Spin } from "antd";

const UsersTable = () => {
  const [isVisibleModal, setIsVisibleModal] = useState(false);
  const [isDataForEdit, setDataForEdit] = useState(false);
  const [userTitleForm, setUserTitle] = useState("Add User");

  const [useUser, setUser] = useState("");
  const users = useSelector((state) => state.adminUsers.data);
  const dispatch = useDispatch();

  useEffect(() => {
    // dispatch(USER_ADMIN.request());
  }, []);

  console.log("users", users);
  const dataSource = [
    {
      key: "1",
      name: "Mike",
      age: 32,
      address: "10 Downing Street",
    },
    {
      key: "2",
      name: "John",
      age: 42,
      address: "10 Downing Street",
    },
  ];
  const columns = [
    {
      title: "ID",
      dataIndex: "userId",
      key: "userId",
    },
    {
      title: "Ime",
      dataIndex: "firstName",
      key: "firstName",
    },
    {
      title: "Prezime",
      dataIndex: "lastName",
      key: "lastName",
    },
    {
      title: "Korisnicko ime",
      dataIndex: "userName",
      key: "userName",
    },
    {
      title: "Email",
      dataIndex: "email",
      key: "email",
    },
    {
      title: "Telefon",
      dataIndex: "phone",
      key: "phone",
    },
    {
      title: "Uredi",
      dataIndex: "Edit",
      key: "Edit",
      render: (text, data) => (
        <Button type="primary" onClick={() => handlerForEdit(data)}>
          Edit
        </Button>
      ),
    },
    {
      title: "Obrisi",
      dataIndex: "Delete",
      key: "Delete",
      render: () => (
        <Button type="primary" danger>
          Delete
        </Button>
      ),
    },
  ];

  const handlerForEdit = (userForEdit) => {
    setUserTitle(
      "Edit User : " + userForEdit.firstName + " " + userForEdit.lastName
    );
    setIsVisibleModal(true);
    setDataForEdit(true);
    setUser(userForEdit);
  };

  const resetForm = () => {
    dispatch(USER_ADMIN.request());
    setIsVisibleModal(false);
  };

  return (
    <div>
      <Table
        pagination={{ pageSize: 10 }}
        dataSource={dataSource}
        columns={columns}
      />
      <Modal
        title={userTitleForm}
        visible={isVisibleModal}
        onCancel={resetForm}
        width={1300}
        footer={false}
      >
        <UserForm initialValues={useUser} editableForm={isDataForEdit} />
      </Modal>
    </div>
  );
  // } else {
  //   console.log("ELSE");
  //   return (
  //     <div className="example">
  //       <Spin size="large" />
  //     </div>
  //   );
  // }
};

export default UsersTable;
