import { React, Input, Form } from "../../../../global";
import { formItemLayout } from "../User/formItemLayout";

const FormItem = Form.Item;
export const renderInput = (filed) => {
  console.log("filed", filed);
  return (
    <FormItem {...formItemLayout} label={filed.label}>
      <Input
        {...filed.input}
        name={filed.input.name}
        placeholder={filed.placeholder}
      />
      {filed.meta.touched && filed.meta.error && (
        <span className="text-danger">{filed.meta.error}</span>
      )}
    </FormItem>
  );
};
