import React from 'react';
import { Card, Row, Col, } from 'antd';
import {
    useEffect,
    useDispatch
} from '../../../global';

const IngredientCard = () => {
    const dispatch = useDispatch();
    useEffect(() => {
        const { showModal, editabileForm, blogForEdit, blogsMessage } = props;
        setIsVisibleModal(showModal);
        setEditabileForm(editabileForm);
        setBlogForEdit(blogForEdit);
        showMessage(blogsMessage);
    }, []);
    return (
        <div>
            <Row>
                {this.props.ingredient.map((data, index) => {
                    return (

                        <Col span={6} key={index} >
                            <Card title={data.name} hoverable={true} gridStyle >
                                <p>Price : {data.price}</p>
                                <p>Time to make pizza : {data.time}</p>
                            </Card>
                        </Col>
                    );
                })}
            </Row>
        </div>
    );
};

export default IngredientCard;
