import { ORDER } from "../../actions/public/ordersAction/ordersActionTypes";

const initialState = {
    orders: { data: [] },
    fetching: false,
    success: false,
    error: null,
};

export default function orderReducer(state = initialState, action) {
    switch (action.type) {
        case ORDER.RECEIVE:
            return {
                ...state,
                orders: {
                    data: [...state.orders.data, action.payload.orderResponse],
                },
                success: true,
                errorMessage: null,
            };
        case ORDER.GET:
            return {
                ...state,
            };
        case ORDER.SUCCESS:
            return {
                ...state,
                data: action.payload.data.orders,
                fetching: true,
                success: true,
                error: null,
            };
        case ORDER.FAILURE:
            break;
        default:
            return {
                ...state,
            };
    }
}
