    const posts = useSelector((state) => state.postsByTerm);

    return (
        <div>
            <h1 className="post_heading">Posts</h1>
            {posts ? posts.map((post) => <h1>{post.entityLable}</h1>) : <h1>no posts</h1>}
        </div>
    );
};
