import { createAction } from "../../../../utils/index";
import { ORDERS } from "./ordersActionTypes";

export const ordersAction = {
    request: () =>
        createAction(ORDERS.GET, {
            fetching: true,
            success: false,
            error: null,
        }),
    success: (data) =>
        createAction(ORDERS.SUCCESS, {
            ...data,
            fetching: true,
            success: true,
            error: null,
        }),
    failure: (error) =>
        createAction(ORDERS.FAILURE, {
            ...error,
            fetching: false,
            success: false,
        }),
};

export default ordersAction;
