import { INGREDIENT } from "../../actions/public/ingredientAction/ingredientActionTypes";

const initialState = {
    ingredients: [],
    fetching: false,
    success: false,
    error: null,
};

export default function ingredientReducer(state = initialState, action) {
    switch (action.type) {
        case INGREDIENT.GET:
            return {
                ...state,
            };
        case INGREDIENT.SUCCESS:
            return {
                ...state,
                ingredients: action.payload.data.ingredients,
                blogs: {
                    resultData: [action.payload.data.ingredients],
                },
                fetching: true,
                success: true,
                error: null,
            };
        case INGREDIENT.FAILURE:
            break;
        default:
            return {
                ...state,
            };
    }
}
