import React from 'react';
import { Card, Row, Col, } from 'antd';
import {
    useEffect,
} from '../../../global';
import { useSelector, useDispatch } from "react-redux";
import INGREDIENT from "../../../redux/actions/public/ingredientAction";


const IngredientCard = () => {
    const users = useSelector((state) => state.adminUsers.users);
    console.log("USERI NA TABLEI", users);
    const dispatch = useDispatch();
    return (
        <div>
            <Row>
                {this.props.ingredient.map((data, index) => {
                    return (

                        <Col span={6} key={index} >
                            <Card title={data.name} hoverable={true} gridStyle >
                                <p>Price : {data.price}</p>
                                <p>Time to make pizza : {data.time}</p>
                            </Card>
                        </Col>
                    );
                })}
            </Row>
        </div>
    );
};

export default IngredientCard;

const mapDispatchToProps = dispatch => {
    return bindActionCreators({ addProduct }, dispatch);
};

connect(null,
    mapDispatchToProps
)(IngredientCard);
