import {
  React,
  connect,
  Menu,
  Layout,
  Component,
  Route,
  Switch,
  Link,
  UserOutlined,
} from "../../../src/global";
import AdminHeader from "../../components/admin-components/header/AdminHeader";
import dashboardRoutes from "../../helpers/DashboardRoutes";
import USER_ADMIN from "../../redux/actions/admin/userAction";

const { Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class AdminPage extends Component {
  state = {
    collapsed: false,
  };

  onCollapse = (collapsed) => {
    this.setState({ collapsed });
  };
  render() {
    return (
      <div>
        <AdminHeader />
        <Layout style={{ minHeight: "56.4rem" }}>
          <Sider
            collapsible
            collapsed={this.state.collapsed}
            onCollapse={this.onCollapse}
          >
            <Menu theme="dark" defaultSelectedKeys={["0"]} mode="inline">
              <SubMenu
                key="sub1"
                title={
                  <span>
                    <UserOutlined />
                    Korisnici
                  </span>
                }
              >
                {dashboardRoutes.map((data, index) => {
                  return (
                    <Menu.Item key={index}>
                      <span> {data.icon}</span>
                      <Link to={data.link}></Link>
                      <span>{data.menuName}</span>
                    </Menu.Item>
                  );
                })}
              </SubMenu>
            </Menu>
          </Sider>
          <Layout>
            <Content style={{ margin: "1.5rem 1.5rem" }}>
              <Switch>
                {dashboardRoutes.map((route, index) => {
                  return (
                    <Route
                      key={index}
                      path={route.path}
                      component={route.component}
                      exact
                    />
                  );
                })}
              </Switch>
            </Content>
            <Footer style={{ textAlign: "center" }}></Footer>
          </Layout>
        </Layout>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log("GLOBALNI STATEEEEE", state);

  const fetchingSuccess = state.adminUsers.success;
  console.log("fetchingSuccess", fetchingSuccess);
  // Redux Store --> Component
  if (fetchingSuccess) {
    const userAdminState = state;
    console.log("userAdminState state", state);
    return {
      adminUsers: userAdminState.adminUsers,
    };
  }
};

// anything returned from this function will end up as props
function mapDispatchToProps(dispatch) {
  return {
    getAdminUsersAction: dispatch(USER_ADMIN.request()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(AdminPage);
