import { ORDER } from "../../actions/public/ordersAction/ordersActionTypes";

const initialState = {
    data: [],
    fetching: false,
    success: false,
    error: null,
};

export default function orderReducer(state = initialState, action) {
    switch (action.type) {
        case ORDER.RECEIVE:
            const nextState = { ...state };
            action.payload.responseOrder.forEach(data => {
                nextState[data.id] = data
            });
            return nextState
        // return {
        //     ...state,
        //     orders: [...state.data, action.payload],
        //     success: true,
        //     errorMessage: null,
        // };
        case ORDER.GET:
            return {
                ...state,
            };
        case ORDER.SUCCESS:
            return {
                ...state,
                data: action.payload.data.orders,
                fetching: true,
                success: true,
                error: null,
            };
        case ORDER.FAILURE:
            break;
        default:
            return {
                ...state,
            };
    }
}
