import {
  React,
  Field,
  reduxForm,
  useDispatch,
  Button,
} from "../../../../global";
import { reset } from "redux-form";
import { renderInput } from "../form-component/index";
import { validate } from "../form-validation/index";
import USER_ADMIN from "../../../../redux/actions/admin/userAction/index";
import { Redirect } from "react-router";

let UserForm = (props) => {
  const dispatch = useDispatch();
  console.log("props", props);

  const onSubmitForm = (values) => {
    console.log("values", values);
    const editableForm = props.editableForm;
    console.log("props", props);
    console.log("editableForm", editableForm);
    if (editableForm) {
      dispatch(USER_ADMIN.requestOne(values));
    } else {
      console.log("usao da dodam");
      dispatch(USER_ADMIN.save(values));
      return <Redirect to="/users" />;
    }
  };

  const { handleSubmit } = props;
  return (
    <form onSubmit={handleSubmit(onSubmitForm)}>
      <div>
        <div>
          <Field
            label="Ime"
            name="firstName"
            component={renderInput}
            type="text"
            placeholder="Unesite ime"
          />
          <Field
            label="Prezime"
            name="lastName"
            component={renderInput}
            type="text"
            placeholder="Unesite prezime"
          />
          <Field
            label="Email"
            name="email"
            component={renderInput}
            type="text"
            placeholder="Unesite email"
          />
          <Field
            label="Telefon"
            name="phone"
            component={renderInput}
            type="text"
            placeholder="Unesite telefon"
          />
          <Field
            label="Sifra"
            name="password"
            component={renderInput}
            type="text"
            placeholder="Unesite Sifru"
          />
          <Field
            label="Korisnicko ime"
            name="userName"
            component={renderInput}
            type="text"
            placeholder="Unesite korisnicko ime"
          />
        </div>
      </div>
      <Button type="primary" htmlType="submit">
        Submit
      </Button>
    </form>
  );
};

// redux-form
UserForm = reduxForm({
  form: "userForm",
  validate,
})(UserForm);

// Important: connect only after calling reduxForm!!!
export default UserForm;
