import { INGREDIENT } from "../../actions/public/ingredientAction/ingredientActionTypes";

const initialState = {
    ingredient: [],
    fetching: false,
    success: false,
    error: null,
};

export default function ingredientReducer(state = initialState, action) {
    switch (action.type) {
        case INGREDIENT.GET:
            return {
                ...state,
            };
        case BLOG.RECEIVE:
            return {
                ...state,
                blogs: {
                    resultData: [...state.blogs.resultData, action.payload.blogResponse],
                },
                success: true,
                errorMessage: null,
            };
        case INGREDIENT.SUCCESS:
            return {
                ...state,
                ingredient: action.payload.data,
                fetching: true,
                success: true,
                error: null,
            };
        case INGREDIENT.FAILURE:
            break;
        default:
            return {
                ...state,
            };
    }
}
