import { ORDER } from "../../actions/public/ordersAction/ordersActionTypes";

const initialState = {
    data: {
        test: '',
        fetching: true,
        success: true,
        error: null,
    }
};

export default function orderReducer(state = initialState, action) {
    switch (action.type) {
        case ORDER.RECEIVE:
            return {
                // ...state,
                // data: [...state.data.orders, action.payload.orderResponse],
                // success: true,
                // errorMessage: null,
                orders: {
                    test: 'nikola'
                }
            };
        case ORDER.GET:
            return {
                ...state,
            };
        case ORDER.SUCCESS:
            return {

                data: {
                    test: action.payload.data.orders,
                    fetching: true,
                    success: true,
                    error: null,
                }
            };
        case ORDER.FAILURE:
            break;
        default:
            return {
                ...state,
            };
    }
}
