export * from 'gl-matrix/vec2';
/**
 * 向量 v1 到 向量 v2 夹角的方向
 * @param  {Array} v1 向量
 * @param  {Array} v2 向量
 * @return {Boolean} >= 0 顺时针 < 0 逆时针
 */
export declare function direction(v1: number[], v2: number[]): number;
/**
 *
 * @param v1
 * @param v2
 * @param direct
 */
export declare function angleTo(v1: number[], v2: number[], direct: boolean): number;
/**
 *
 * @param out
 * @param v
 * @param flag
 */
export declare function vertical(out: number[], v: number[], flag: boolean): number[];
